	private int nextHead(String s, int head, int[] array, int maxLen) {
		final int length = s.length();
		for(head++; head<length && s.charAt(head)!='(' && !isPossible(array, head, maxLen); head++);
		return head;
	}
	
	private boolean isPossible(int[] array, int head, int maxLen) {
		int length = array.length-head, nH, nE;
		if(length<=maxLen) return false;
		do {
			nE = array[head]-array[head+length-1];
			nH = length-nE;
			if(nH<nE) length = 2*nH;
			else length = 2*nE;
		} while (nH!=nE && length>0);
		if(length>maxLen) return true;
		else return false;
	}
	
