	public int longestValidParentheses(String s) {
		final int length = s.length();
		int maxLen = 0, head, lens[] = new int[length+1];
		ArrayList<Integer> stack = new ArrayList<Integer>(length);

		for(int i = 0; i<length; i++) {
			lens[i+1] = 0;
			if(s.charAt(i)=='(') stack.add(0, i);
			else {
				if(!stack.isEmpty()) {
					head = stack.remove(0);
					lens[i+1] = i-head+lens[head]+1;
					if(lens[i+1]>maxLen) maxLen = lens[i+1];
				}
			}
		}
		return maxLen;
	}
	
	
